import React, { useEffect, useState} from 'react';
import { StyleSheet, Text, View, Dimensions } from 'react-native';

import Taco from './components/Taco';
import Obstacles from './components/obstacles';

export default function App() {
  //get screen width and height
  const screenWidth = window.innerWidth
  const screenHeight = window.innerHeight
  const tacoLeft = screenWidth / 2
  const [tacoBottom, setTacoBottom] = useState(screenHeight / 2)
  const [obstaclesLeft, setObstaclesLeft] = useState(screenWidth)
  const [obstaclesLeftTwo, setObstaclesLeftTwo] = useState(screenWidth + screenWidth/3)
  const [obstaclesLeftThree, setObstaclesLeftThree] = useState(screenWidth + screenWidth/6)
  const [obstaclesNegHeight, setObstaclesNegHeight] = useState(0)
  const [obstaclesNegHeightTwo, setObstaclesNegHeightTwo] = useState(0)  
  const [obstaclesNegHeightThree, setObstaclesNegHeightThree] = useState(0)   

  const obstacleWidth = 60
  const obstacleHeight = 300
  const gap = 180
  const obstacleWidthTwo = 80
  const obstacleHeightTwo = 300
  const gapTwo = 180
  const obstacleWidthThree = 60
  const obstacleHeightThree = 300
  const gapThree = 180

  let gameTimerId 
  let obstaclesLeftTimerId
  let obstaclesLeftTimerIdTwo
  let obstaclesLeftTimerIdThree

  //start taco falling
  useEffect(() => {
    if(tacoBottom > 0){
      gameTimerId = setInterval(() =>{
        setTacoBottom(tacoBottom => tacoBottom - 3)
      }, 30)

      return()=>{
        clearInterval(gameTimerId)
      }
    }
  }, [tacoBottom])
  console.log(tacoBottom)

//start first obstacles
  useEffect(() => {
    if(obstaclesLeft > -obstacleWidth){
      obstaclesLeftTimerId = setInterval(() => {
        setObstaclesLeft(obstaclesLeft => obstaclesLeft - 5)
      }, 30)

      return() => {
        clearInterval(obstaclesLeftTimerId)
      }
    } else {
      setObstaclesLeft(screenWidth)
      setObstaclesNegHeight(-Math.random()*100)
    }  
  }, [obstaclesLeft])

  //start second obstacles
  useEffect(() => {
    if(obstaclesLeftTwo > -obstacleWidth){
      obstaclesLeftTimerIdTwo = setInterval(() => {
        setObstaclesLeftTwo(obstaclesLeftTwo => obstaclesLeftTwo - 5)
      }, 32)

      return() => {
        clearInterval(obstaclesLeftTimerIdTwo)
      }
    } else {
      setObstaclesLeftTwo(screenWidth)
      setObstaclesNegHeightTwo(-Math.random()*100)
    }  
  }, [obstaclesLeftTwo])

  //start third obstacles
  useEffect(() => {
    if(obstaclesLeftThree > -obstacleWidth){
      obstaclesLeftTimerIdThree = setInterval(() => {
        setObstaclesLeftTwo(obstaclesLeftThree => obstaclesLeftThree - 5)
      }, 30)

      return() => {
        clearInterval(obstaclesLeftTimerIdThreeobstaclesLeftThree)
      }
    } else {
      setObstaclesLeftThreeobstaclesLeftThree(screenWidth)
      setObstaclesNegHeightThreeobstaclesLeftThree(-Math.random()*100)
    }  
  }, [obstaclesLeftThree])



  return (
    <View style={styles.container}>
      <Taco 
        tacoBottom={tacoBottom}
        tacoLeft={tacoLeft}
      />
      <Obstacles
      color={'#F5853F'}
      obstaclesLeft={obstaclesLeft}
      obstacleWidth={obstacleWidth}
      obstacleHeight={obstacleHeight}
      randomBottom={obstaclesNegHeight}
      gap={gap}
      />
      <Obstacles
      color={'#0091Ad'}
      obstaclesLeft={obstaclesLeftTwo}
      obstacleWidth={obstacleWidthTwo}
      obstacleHeight={obstacleHeightTwo}
      randomBottom={obstaclesNegHeightTwo}
      gap={gapTwo}
      />
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
